import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-product-details',
  standalone: false,
  templateUrl: './product-details.html',
  styleUrl: './product-details.css',
})
export class ProductDetails { 
  products = [
    { 'id': 'p1', 'name': 'COCA', 'price': 15, 'image': 'data:image/jpeg;base64,' },
    { 'id': 'p2', 'name': 'PESI', 'price': 15, 'image': 'data:image/jpeg;base64,' },
    { 'id': 'p3', 'name': 'RED BULL', 'price': 12, 'image': 'data:image/jpeg;base64,' },
    { 'id': 'p4', 'name': 'Aqua', 'price': 10, 'image': 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTEQnwqSHYnifX3GhFVzP9OGJ3V7AVcK2SzWQ&s' },
    { 'id': 'p4', 'name': 'Lavie', 'price': -10, 'image': 'data:image/jpeg;base64,' }
  ];
product_selected: {id: string, name: string, price: number, image: string} = this.products[0];
  constructor(private router: Router, private activeRoute: ActivatedRoute) {
    //Dung router de dieu huong trang
    //Dung activeRoute de lay tham so tren url
    activeRoute.paramMap.subscribe((param) => {
      let id = param.get('id');
      this.product_selected = this.products.find(p => p.id == id)!;
    })
  }
  go_back() {
    this.router.navigate(['san-pham-1', { id: this.product_selected.id}]);
  } 
} 
